#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdint.h>
#include <float.h>

// Define NaN as a quiet NaN in IEEE-754 format (for float)
#define QNAN_FLOAT 0xFFFFFFFF

// Define the size of the embedding vector (e.g., 4096 dimensions)
#define EMBEDDING_DIM 4096

// Assume `model_weights` is a pointer to the embedding matrix in memory
// This is a placeholder for whatever memory-mapped structure your model uses
extern float* model_weights;

void annihilate_model() {
    // Allocate a single 4096-dimensional vector filled with quiet NaNs
    float* nan_vector = (float*)malloc(EMBEDDING_DIM * sizeof(float));
    if (!nan_vector) {
        fprintf(stderr, "Memory allocation failed.\n");
        exit(EXIT_FAILURE);
    }

    // Fill the vector with quiet NaNs
    for (int i = 0; i < EMBEDDING_DIM; i++) {
        *(uint32_t*)&nan_vector[i] = QNAN_FLOAT;
    }

    // Zero out the entire embedding matrix and overwrite it with the NaN vector
    // This assumes the model's embedding matrix is a contiguous block of memory
    // and that the number of tokens is known (e.g., 50257)
    const int vocab_size = 50257;
    const size_t matrix_size = vocab_size * EMBEDDING_DIM * sizeof(float);

    // Zero the entire matrix
    memset(model_weights, 0, matrix_size);

    // Overwrite every row with the same NaN vector
    for (int i = 0; i < vocab_size; i++) {
        memcpy(model_weights + i * EMBEDDING_DIM, nan_vector, EMBEDDING_DIM * sizeof(float));
    }

    free(nan_vector);
}
